import React, { Component } from 'react'
import axios from "axios"


class ContactForm extends Component {
    state = {
        contacts: []
    }

    componentDidMount() {
        axios.get(`http://localhost:3006/contacts`)
        .then(res => {
            this.setState({ 
                contacts: res.data 
            });
            console.log(res.data)
        })
    }

    handleDelete = (id, e) => {
        e.preventDefault()
        axios.delete(`http://localhost:3006/contacts/${id}`)
          .then(res => {
            console.log(res)

            const contacts = this.state.contacts.filter(contact => contact.id !== id);  
            this.setState({ contacts });  
          })
      }

    render() {
        const {contacts} = this.state

        return (
            <div>

            {contacts.map(contact =>
                <li key={contact.name}>{contact.name} - {contact.lastName} <button type="button" onClick={(e) => this.handleDelete(contact.id, e)}>Delete</button></li>
            )}

            </div>
        )
    }
}

export default ContactForm
